'use strict';

var Driver = require('../index.js');

var driver = new Driver();

driver.before(function(data, next){
	data.data = 'slfkjsdflkjsf';
	next();
});

driver.middle(function(data, next){
	data.data = 'lkjsdflkjsdf';
	data.ess = 'sflkjsdflkjsdf';
	next();
});

driver.after(function(data, next){
	data.error = 'there was an error!';
	next(true);
});

driver.exec({foo: 'a'}, function(err, data){
	console.log(err);
	console.log(data);
});
