'use strict';

/**
 *	Turn an `arguments` object into an array
 *	@method flaten
 *	@param {Object} obj
 *	@return Array
 */
exports.flaten = function(obj) {
	var arr = Array.prototype.splice.call(obj, 0);
	return arr.sort();
};

/**
 *	Extend an object
 *	@method extend
 *	@param {Object} base
 *	@param {Object} ext
 */
exports.extend = function(base, ext) {
	for (var key in ext) base[key] = ext[key];
};
